#1.connect database
import mlab
from mongoengine import Document, StringField, IntField

mlab.connect()
# 2. define model
class Movie(Document):
    title = StringField()
    image = StringField()
    link = StringField()
    rate = IntField()

movie_list = Movie.objects(rate__gte=7, title__contains="Infinity")   #Lazy loading
for m in movie_list:
    print(m.title, m.rate)

